import { BehaviorSubject } from 'rxjs';
import * as i0 from "@angular/core";
import * as i1 from "@services";
export class PokemonService {
    constructor(requestHandlerService) {
        this.requestHandlerService = requestHandlerService;
        this.pokemonData = new BehaviorSubject({});
        this.pokemonDataPagination = new BehaviorSubject({});
    }
    async getPokemonBasicData(offset, limit) {
        return await this.requestHandlerService
            .doGet(`pokemon/data/basic/?&offset=${offset}&limit=${limit}`)
            .catch(err => {
            throw err;
        });
    }
    async getPokemonDetailData(pokemonName) {
        return await this.requestHandlerService
            .doGet(`pokemon/data/detail/${pokemonName}`)
            .catch(err => {
            throw err;
        });
    }
}
